const strings=require("../lang/localization.json"),languages=["en","ru"];export function getLocale(){return navigator.languages&&navigator.languages.length?navigator.languages[0]:navigator.language}export function switchLang(n){let a;for(let e in strings.language)a=n.innerHTML===strings.language[e]?e:a;return a=languages[languages.indexOf(a)+1>=languages.length?0:languages.indexOf(a)+1],a}export async function localize(n){languages.includes(n)||(n="en");for(let a in strings)document.querySelectorAll("#"+a).forEach((e=>{e.innerHTML=strings[a][n]}))}